<?php class Form_Items extends Form_SuperForm {    public function init() {           	$this->Text('code', 'الكود : ', false);        $modelBlocks = new Model_PlusData();        $modelBlocks->_name = 'cat';        $lang = Func_Lang::getLang();        $where = array('lang'=>$lang);        $catid = $this->createElement('select', 'catid');        $catid->setLabel('القسم : ');        $catid->setRequired(TRUE);        $catid->addErrorMessage('الحقل اجباري');        $catid->addMultiOption('', '');        foreach ($modelBlocks->selectForArray($where) as $values) {            $catid->addMultiOption($values['id'], $values['title']);        }        $this->addElement($catid);                $this->Text('title', 'العنوان : ', false);        $this->Text('slider_image', 'السعر : ', false);        $this->Text('table_image', 'الصورة : ', false);        $this->TextErea('data', 'الوصف' );        $this->Text('displayorder', 'ترتيب العرض : ', false);        $is_active =  $this->createElement('checkbox','is_home');        $is_active->setAttrib('onclick',"var j = jQuery('#check').attr('disabled', jQuery('#check').attr('disabled') ? false : true)");        $is_active->setLabel('عرض جديد');        $is_active->setAttrib('class','checkboxcecked');        $this->addElement($is_active);        $show_home =  $this->createElement('checkbox','show_home');        $show_home->setLabel('عرض في الرئيسية');        $show_home->setAttrib('class','checkboxcecked');        $this->addElement($show_home);		$this->loadSubmit();    }}