<?php class Form_Register extends Form_SuperForm {    public function init() {        $username = $this->createElement('text', 'username');        $username->setRequired(true);        $username->setLabel('اسم المستخدم : ');        $username->addValidator(new Zend_Validate_Db_NoRecordExists(array(                                'table' => 'users',                                'field' => 'username',                                'messages' => array(                                        'recordFound' => 'اسم المستخدم محجوز'                                )                        )));        $username->addErrorMessage('الحقل اجباري');        $this->addElement($username);                $password = $this->createElement('password', 'password');        $password->setRequired('true');        $password->setLabel('كلمة المرور : ');        $password->addErrorMessage('الحقل اجباري');        $password->setAttrib('class', 'logninptxxpass');        $this->addElement($password);                $frmPassword2=$this->createElement('password', 'confirm_password');        $frmPassword2->setLabel('تأكيد الكلمة:')            ->setRequired('true')            ->addErrorMessage('الحقل اجباري')            ->addFilter(new Zend_Filter_StringTrim())            ->addValidator(new Zend_Validate_Identical(Zend_Controller_Front::getInstance()->getRequest()->getParam('password')));            $this->addElement($frmPassword2);                $email = $this->createElement('text', 'email');        $email->setRequired('true');        $email->addValidator(new Zend_Validate_EmailAddress());        $email->addValidator(new Zend_Validate_Db_NoRecordExists(array(                                'table' => 'users',                                'field' => 'email',                                'messages' => array(                                        'recordFound' => 'البريد الإلكتروني محجوز'                                )                        )));        $email->addFilters(array(new Zend_Filter_StringTrim(), new Zend_Filter_StringToLower()));        $email->addErrorMessage('الحقل اجباري');        $email->setAttrib('class', 'textinput');        $email->setLabel('البريد الإلكتروني : ');        $this->addElement($email);                $phone = $this->createElement('text', 'phone');        $email->setRequired('true');        $phone->addErrorMessage('الحقل اجباري');        $phone->setAttrib('class', 'textinput');        $phone->setLabel('رقم الجوال:- ');        $this->addElement($phone);                 $this->loadSubmit();                $this->setElementDecorators(array('ViewHelper',            'Description',            'Errors'));    }}