<?php class Form_RequestSearch extends Form_SuperForm {    public function init() {                       $modelBlocks = new Model_PlusData();        $modelBlocks->_name = 'cat';        $lang = Func_Lang::getLang();        $where = array('lang'=>$lang , 'type'=>$this->options['type']);        $catid = $this->createElement('select', 'catid');        $catid->setLabel('القسم : ');        $catid->addMultiOption('', '');        foreach ($modelBlocks->selectForArray($where) as $values) {            $catid->addMultiOption($values['id'], $values['title']);        }        $this->addElement($catid);                        $this->loadSubmit();    }}